import { useState } from "react"
import '../components/PostMoney.css'


const PostMoney = () => {

    const [form, setForm] = useState({
        USD: "", senderName: "", receiverName: "", purpose: ""
    })
    let name , value;
    const handleInput = (e) => {
        
        name = e.target.name
        value = e.target.value
    
        setForm({ ...form, [name]: value })
    
      }
    const postData =  async (e) => {
 
